    mov.l r3, @r4                          ! scene_active_flag = 0
    bra     .L_epilogue
    nop
.L_set_active:
    mov.l r10, @r4                         ! scene_active_flag = 1
.L_epilogue:
    lds.l @r15+, pr                        ! restore registers
    mov.l @r15+, r10
    mov.l @r15+, r11
    mov.l @r15+, r12
    mov.l @r15+, r13
    rts
    mov.l @r15+, r14
