    mov #0x4, r4
.L_post_race_render:                     /* === Post-race: render current selection === */
    mov.l @r13, r2                      /* r2 = active_slot_index */
    cmp/pz r2                           /* valid slot? (>= 0) */
    bt      .L_post_race_physics        /* yes → render */
    bra     .L_exit                     /* no → exit */
